import React, { useState } from "react";
import Modal from "./modal";
import Login from "./login";
import Signup from "./signup";
import "../Components/style.css";

const HomePage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSigningUp, setIsSigningUp] = useState(false);

  const handleOpenModal = (signingUp) => {
    setIsSigningUp(signingUp);
    setIsModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* Navigation Bar */}
      <nav className="nav-wrapper">
        <div className="logo">SESDC/LOGO HERE</div>
        <div className="nav-center">
          <ul className="nav-links">
            <li><a href="#" className="nav-link">Home</a></li>
            <li><a href="#" className="nav-link">About</a></li>
            <li><a href="#" className="nav-link">Help</a></li>
            <li><a href="#" className="nav-link">Contact Us</a></li>
          </ul>
        </div>
        <div className="button-container">
          <button className="register-btn" onClick={() => handleOpenModal(true)}>Sign Up</button>
          <button className="login-btn" onClick={() => handleOpenModal(false)}>Login</button>
        </div>
      </nav>

      {/* Main Section */}
      <section className="hero-section">
        <div className="content-box">
          <h1 className="title">A user-friendly microgrid design tool</h1>
          <button className="design-tool-button">
            Open Design Tool
          </button>
        </div>
      </section>

      {/* Overview Section */}
      <section className="overview-section">
        <p>Understanding Our Project: Learn why we created this tool and how it benefits users.</p>
        <button className="get-started">Get Started!</button>
      </section>

      {/* Login/Signup Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        {isSigningUp ? (
          <Signup onSignup={() => setIsModalOpen(false)} onToggle={() => setIsSigningUp(false)} />
        ) : (
          <Login onLogin={() => setIsModalOpen(false)} onToggle={() => setIsSigningUp(true)} />
        )}
      </Modal>
    </div>
  );
};

export default HomePage;
