import React, { useState } from "react";
import Modal from "./Modal";
import Login from "./Login";
import Signup from "./Signup";

const HomePage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSigningUp, setIsSigningUp] = useState(false);

  const handleOpenModal = (signingUp) => {
    setIsSigningUp(signingUp);
    setIsModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* Navigation Bar */}
      <nav className="bg-gray-200 p-4 w-full shadow-md flex justify-between items-center">
        <div className="font-bold text-lg">SESDC/LOGO HERE</div>
        <ul className="flex space-x-8">
          <li><button className="nav-link">Home</button></li>
          <li><button className="nav-link">About</button></li>
          <li><button className="nav-link">Help</button></li>
          <li><button className="nav-link">Contact Us</button></li>
        </ul>
        <div className="flex space-x-3">
          <button className="register-btn" onClick={() => handleOpenModal(true)}>
            Sign Up
          </button>
          <button className="login-btn" onClick={() => handleOpenModal(false)}>
            Login
          </button>
        </div>
      </nav>

      {/* Main Section */}
      <section className="hero-section">
        <div className="content-box">
          <h1 className="title">A user-friendly microgrid design tool</h1>
          <button className="design-tool-button">
            Open Design Tool
          </button>
        </div>
      </section>

      {/* Overview Section */}
      <section className="overview-section">
        <p>Understanding Our Project: Learn why we created this tool and how it benefits users.</p>
        <button className="get-started">Get Started!</button>
      </section>

      {/* Login/Signup Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        {isSigningUp ? (
          <Signup onSignup={() => setIsModalOpen(false)} onToggle={() => setIsSigningUp(false)} />
        ) : (
          <Login onLogin={() => setIsModalOpen(false)} onToggle={() => setIsSigningUp(true)} />
        )}
      </Modal>
    </div>
  );
};

export default HomePage;
